    def updateBoard(self, board, click):
        self.board = board
        def dfs(i, j):
            if i < 0 or j < 0 or i == len(board) or j == len(board[i]):
                return
            if self.board[i][j] not in ['E', 'M']:
                return
            if self.board[i][j] == 'M':
                self.board[i][j] = 'X'
                return
            else:
                cnt = 0
                direct = [[1,0],[1,-1],[1,1],[-1,1],[-1,0],[-1,-1],[0,1],[0,-1]]
                for d in direct:
                    cnt += detect(i+d[0], j+d[1])
                if cnt == 0:
                    self.board[i][j] = 'B'
                    for d in direct:
                        dfs(i+d[0], j+d[1])
                else:
                    self.board[i][j] = str(cnt)
        
        def detect(i, j):
            if i < 0 or j < 0 or i == len(board) or j == len(board[i]):
                return 0
            if self.board[i][j] in ['X','M']:
                return 1
            return 0
        
        dfs(click[0], click[1])
        return self.board
